package com.example.motodescriptive;

import android.content.Intent;
import android.os.Bundle;

import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.SearchView;

import java.util.ArrayList;

public class RecFragment extends Fragment implements MotorAdapter.OnNoteClicked {

    private ArrayList<MotorItems>motorcycle;
    private RecyclerView recyclerView;
    private MotorAdapter adapter;
    private SearchView searchView;
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        View v = inflater.inflate(R.layout.fragment_rec, container, false);
        recyclerView = v.findViewById(R.id.recyclerView);
        searchView = v.findViewById(R.id.search_bar);

        motorcycle = new ArrayList<>();
        motorcycle.add(new MotorItems("Yamaha YZF R125", "125cc motorbike with 11kw and 15hp.", "This remarkable motorcycle has been developed using the same advanced technology that makes our larger capacity R-series models some of the most respected bikes on the street and track.\n" +
                "\n" +
                "With its radical bodywork and features like upside down front forks and an aluminium swinging arm - as well as a short-stroke liquid-cooled engine and ABS - the R125 is built to thrill.", "https://cdn2.yamaha-motor.eu/prod/product-assets/2017/YZF-R125/2017-Yamaha-YZF-R125-EU-Radical_Red-Studio-001-03.jpg"));
        motorcycle.add(new MotorItems("Honda CBR 125R", "125cc motorbike very nice", "The Honda CBR125R is a CBR series 125 cc (7.6 cu in) single-cylinder sport bike made by Honda. The CBR125R first appeared on the market in 2004. It is manufactured in Thailand by A.P. Honda alongside the similarly designed CBR150R, which is primarily aimed for the Far East market.\n" +
                "\n" +
                "It has the smallest displacement engine of any CBR motorcycle.", "data:image/jpeg;base64,"));
        motorcycle.add(new MotorItems("Ktm Duke 125", "The best naked bike", "The KTM 125 DUKE may be the entry-level bike of the range, but it isn't here to play games. It's built with the same attitude as the bigger bikes - and it shows. It's no holds barred with this urban warrior, complete with an LED headlight and TFT display. This 4-stroke single cylinder has fuel-injection and a 6-speed transmission to deliver class-leading performance with low fuel consumption to keep you riding longer.","https://bike-tests.s3.amazonaws.com/media/BikeImage/65/ktmduke1-0.png"));
        motorcycle.add(new MotorItems("Kawasaki Z900", "Good as always", "The Z900 ABS motorcycle offers a host of advanced features and epitomizes Kawasaki’s belief of what the ideal supernaked should be. At 948cc with an ultra-lightweight chassis, every ride is met with exceptional power, responsiveness and excitement.\n" +
                "\n" +
                "FEATURING:\n" +
                "948cc LIQUID-COOLED, INLINE FOUR-CYLINDER ENGINE\n" +
                "STRONG FRAME\n" +
                "OPTIMIZED FRONT & REAR SUSPENSION SETTINGS\n" +
                "SUGOMI™-INSPIRED Z STYLING","https://images.avto.net/photo/15032188/1058945.jpg"));
        motorcycle.add(new MotorItems("Aprilia Dorsoduro 750", "My cousin has this bike", "The Aprilia Dorsoduro is priced competitively cheaper than Ducati’s Hypermotard and KTM 990 supermoto, and still turns heads, is laden with quality parts and has clearly been designed and not just thrown together. The Aprilia Dorsoduro may not be as radical as the competition or Aprilia’s marketing department would let us believe, however this does make it easy to ride, not intimidating, making the Dorsa a fun, real world, big capacity supermoto.","https://s1.cdn.autoevolution.com/images/moto_gallery/APRILIADorsoduro750-4773_1.jpg"));
        motorcycle.add(new MotorItems("Aprilia SX125", "Supermoto shit", "A very distinctive motorcycle with high level performance and design. The only bike in this segment to sport a front-wheel-only ABS braking system. Heir of Aprilia’s history of success in off-road competitions, the updated SX 125 boasts a sophisticated design, powerful performance and advanced technology that makes this bike for people who want to stand out from the crowd right from the start of their two-wheel career. The SX offers great riding satisfaction to anyone climbing on-board.","https://crocomoto.com/wp-content/uploads/2017/02/Aprilia-SX-125-2010-photo.jpg"));
        adapter = new MotorAdapter(motorcycle, this, getContext());
        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

        searchView.setOnQueryTextListener(new SearchView.OnQueryTextListener() {
            @Override
            public boolean onQueryTextSubmit(String s) {
                return false;
            }

            @Override
            public boolean onQueryTextChange(String s) {
                adapter.getFilter().filter(s);
                return false;
            }
        });
        return v;
    }

    @Override
    public void OnNote() {
        Intent intent = new Intent(getContext(), Fragments.class);
        startActivity(intent);
    }
}